test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{
	
	workers			= threads
}

source disk
{
	type			= mysql
	
	sql_query		= select * from test_table
	sql_attr_uint	= gid
}

index disk
{
	source			= disk
	path			= /disk
	stored_fields	= title
}

index rt
{
	type			= rt
	path			= data/rt
	rt_attr_uint	= gid
	rt_field		= title
}

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) NOT NULL
);
