test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source test1
{
	type			= mysql

	sql_query		= select id, 1 as gid, title from test_table where id in (1)
	sql_attr_uint	= gid
}

index test1
{
	source			= test1
	path			= /test1
	min_infix_len = 2
}

source test2 : test1
{
	sql_query		= select id, 1 as gid, title from test_table where id in (2,3)
}

source test3 : test1
{
	sql_query		= select id, 1 as gid, title from test_table where id in (4,5)
}

index test2
{
	source			= test2
	path			= /test2
	min_infix_len = 2
}

index test3
{
	source			= test3
	path			= /test3
	min_infix_len = 2
}

index dist1
{
	type = distributed
	agent = 127.0.0.1::test1
	agent = 127.0.0.1::test2
	agent = 127.0.0.1::test3
}

index dist2
{
	type = distributed
	local = test1
	local = test2
	local = test3
}

index dist3
{
	type = distributed
	agent = 127.0.0.1::test1
	agent = 127.0.0.1::test2
	local = test3
}

test.db_create:
create table test_table
(
	id int not null,
	title varchar(255) not null
);
