test.config:
indexer
{
	mem_limit				= 16M
}

searchd
{

	workers = threads

}

source src
{
  type      = mysql

  sql_query     = SELECT id, gid, title FROM test_table
  sql_attr_uint   = gid
}

index test
{
  source      = src
  path      = /test
}

index rt
{
	type = rt
	path = /rt
	rt_field = title
	rt_attr_uint = attr1
	rt_attr_uint = attr2
	rt_attr_uint = attr3
	rt_attr_multi = m1
	rt_attr_multi = m2
	rt_attr_multi = m3
}

index rt1
{
	type = rt
	path = /rt1
	rt_field = title
	rt_attr_uint = attr1
	rt_attr_uint = attr2
    index_field_lengths = 1
}

test.db_create:
CREATE TABLE `test_table` (
  `id` int(11) NOT NULL,
  `gid` int(11) NOT NULL,
  `title` varchar(255) NOT NULL
